import React from 'react';
import '../styles/HomePage.css';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
export const NextTo = styled.div`
    background-image: url(${arrow});
    background-size: contain;
    height: 20px;
    width: 20px;
`

export const Prev = styled.div`
    transform: rotate(180deg);
    background-image: url(${arrow});
    background-size: contain;
    height: 20px;
    width: 20px;
`
 
 
    const HomePage = ({ currentSlide, slideCount, children, ...props }) => {
      var settings = {
        infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1,
            nextArrow: (			//오른쪽 화살표
              <SlickButtonFix>
                <NextTo />
              </SlickButtonFix>
            ),
            prevArrow: (			//왼쪽 화살표
              <SlickButtonFix>
                <Prev />
              </SlickButtonFix>
            )
    
      };
  return (
    <div className="home-container">
      <section className="hero-section">
      
           
            <Slider {...settings}>

      <div>
        <h3>KBO야구타자 TOP5</h3>
      </div>
      <div>
        <h3>KBO야구투수 TOP5</h3>
      </div>
      <div>
        <h3>MLB야구타자 TOP5</h3>
      </div>
      <div>
        <h3>MLB야구투수 TOP5</h3>
      </div>
    </Slider>
  

  
          
        
      </section>
    </div>
  );
};

export default HomePage;
