import React, { useState } from 'react';
import '../styles/App.css';
import styled from "styled-components";
const BaseballGamePage = () => {
  const [gameStarted, setGameStarted] = useState(false);
  const StyledBase = styled.div`
  height: 26px;		// 게이지 부분의 높이에 border 높이의 2배를 더해준다.
  border: 3px solid;
  margin-right: 15%;		// 페이지에서 원하는 부분만큼 자유롭게 설정
  margin-left: 15%;
  justify-content: center;
`

const StyledRange = styled.div`
  width: ${({ width }) => `${width}%`};	// 값을 받아서 %로 바꾸어 설정해준다.
  height: 20px;
  background: linear-gradient(to right, #FFACFC, #B76CFD);	// 그라데이션 설정
`

  const exp = 10000;
  const ratio = parseInt(exp % 100);

  return (
    <div>
    <div className="game-container" style={{ position: 'relative' }}>
      {/* 왼쪽 위에 고정된 팀/상대팀 */}
      <div style={{ position: 'absolute', top: 20, left: 20, zIndex: 10, textAlign: 'left' }}>
        <h2 style={{ margin: 0 }}>팀</h2>
        <h2 style={{ margin: 0 }}>상대팀</h2>
      </div>
      <h1 className="game-title">⚾ Baseball Game</h1>
      <div className="game-info">

        {!gameStarted && (
          <button className="game-button" onClick={() => setGameStarted(true)}>
            게임 시작
          </button>
        )}
        {gameStarted && (
          <div>
            <StyledBase>	{/* Base 안에 Range를 넣어 준다. */}
                <StyledRange width={ratio} />	{/* props를 받아 width를 설정할 수 있게 함 */}
            </StyledBase>
            <h2>게임 중</h2>
          </div>
        )}
      </div>
    </div>
    </div>
  );
};

export default BaseballGamePage;
