import React from 'react';
import { Link } from 'react-router-dom';
import { useUser } from '../contexts/UserContext';
import './Header.css';

const Header = () => {
  const { user, logout } = useUser();

  const handleLogout = async () => {
    const result = await logout();
    if (result.success) {
      alert('로그아웃되었습니다.');
      window.location.href = '/';
    } else {
      alert('로그아웃 중 오류가 발생했습니다.');
    }
  };

  return (
    <header className="header-container">
      <nav className="header-nav">
        <Link to="/" className="header-logo">.</Link>
        <div className="header-nav-links">
          <Link to="/noticeboard" className="header-nav-link">게시판</Link>
          <Link to="/about" className="header-nav-link">BKO</Link>
          <Link to="/services" className="header-nav-link">MLB</Link>
          <Link to="/game" className="header-nav-link">게임</Link>
        </div>
        <div className="header-auth">
          {user ? (
            <div className="user-info">
              <span className="user-name">{user.name}님</span>
              <button onClick={handleLogout} className="logout-button">로그아웃</button>
            </div>
          ) : (
            <Link to="/login" className="header-nav-link">로그인</Link>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
