import React from 'react';
import '../styles/HomePage.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Pagination, Scrollbar, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/scrollbar';

// 임시 포스터 데이터
const posters = [
  { id: 1, title: '포스터1' },
  { id: 2, title: '포스터2' },
  { id: 3, title: '포스터3' }
];

// 임시 Poster 컴포넌트
const Poster = ({ title }) => (
  <div style={{ height: 200, background: '#eee', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
    {title}
  </div>
);

const HomePage = () => {
  return (
    <div className="home-container">
      <section className="hero-section">
       

          <p className="hero-subtitle">
            KBO야구타자 TOP5
           <Swiper
      className="bannerSwiper"
      modules={[Pagination, Scrollbar, Autoplay]}
      spaceBetween={10}
	  // 슬라이드 간의 너비 설정

      slidesPerView={1}
	  // 하나의 슬라이드에 몇 개의 콘텐츠를 보여줄지 설정

      pagination={{ clickable: false }}
      // 페이지네이션 관련 설정
      
      scrollbar={{ draggable: true, hide: true }}
      // 스크롤 관련 설정
      // draggable은 마우스 드래그나 모바일 터치로 슬라이드를 넘길 수 있게 합니다.
      autoplay={{
        delay: 3000,
        disableOnInteraction: false,
      }}
      // 자동으로 슬라이드 넘기기 설정
      
      loop={true}
	  // 마지막 슬라이드에서 슬라이드가 넘어가면 다시 처음으로 갈지 설정
    >
      {posters.map((poster) => (
        <SwiperSlide key={poster.id}>
          <Poster title={poster.title} />
        </SwiperSlide>
      ))}
    </Swiper>
          </p>
          <p className="hero-subtitle">
            MLB야구타자 TOP5 랭킹
          </p>
          
        
      </section>
    </div>
  );
};

export default HomePage;
