import React from 'react';
import '../styles/mlbPage.css';

const [isCategorySelect, setIsCategorySelect] = useState(false);
const notPreferArr =['팀 순위', '선수 순위'];

const handleClick = (idx) => {
	const newArr = Array(notPreferArr.length).fill(false);
    newArr[idx] = true;
	setIsCategorySelect(newArr);
};
const MlbPage = () => {
  return (
    <div className="about-container">
      <h1>순위</h1>
      <div className="player-record-container">
      <li class={isCategorySelect[0] ? 'on' : ''} onClick={() => handleClick(0)} className='team-record'>팀 순위</li>
      <li class={isCategorySelect[1] ? 'on' : ''} onClick={() => handleClick(1)} className='team-record'>선수 순위</li>
      </div>
      <h1>기록</h1>
      <div className="player-record">
      <button className='team-record'>팀 기록</button>
      <button className='team-record'>선수 기록</button>
     </div>
   </div>
  );
};

export default MlbPage;
