import React, { useState } from 'react';
import styled from 'styled-components';
import writing from './writing.js';

const BoardContainer = styled.div`
  max-width: 900px;
  margin: 0 auto;
  padding: 3rem 1rem 4rem 1rem;
`;

const BoardHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;
`;

const Title = styled.h1`
  font-size: 2rem;
  color: #2c3e50;
`;

const WriteButton = styled.button`
  background: #3498db;
  color: white;
  border: none;
  padding: 0.7rem 1.5rem;
  border-radius: 5px;
  font-size: 1rem;
  font-weight: bold;
  transition: background 0.2s;
  &:hover {
    background: #217dbb;
  }
`;

const PostList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.2rem;
`;

const PostCard = styled.div`
  background: white;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.07);
  padding: 1.2rem 1.5rem;
  display: flex;
  flex-direction: column;
  cursor: pointer;
  transition: box-shadow 0.2s;
  &:hover {
    box-shadow: 0 4px 16px rgba(52,152,219,0.15);
  }
`;

const PostTitle = styled.div`
  font-size: 1.15rem;
  font-weight: 600;
  color: #34495e;
  margin-bottom: 0.5rem;
`;

const PostMeta = styled.div`
  font-size: 0.95rem;
  color: #888;
  display: flex;
  gap: 1.5rem;
`;

const Pagination = styled.div`
  display: flex;
  justify-content: center;
  gap: 0.5rem;
  margin-top: 2.5rem;
`;

const PageButton = styled.button`
  background: #f1f1f1;
  border: none;
  color: #3498db;
  padding: 0.5rem 1rem;
  border-radius: 4px;
  font-size: 1rem;
  font-weight: 500;
  cursor: pointer;
  transition: background 0.2s;
  &:hover, &.active {
    background: #3498db;
    color: white;
  }
`;

// 목업 데이터
const mockPosts = [
  { id: 1, title: '첫 번째 자유게시글', author: 'user1', date: '2024-06-01', views: 12 },
  { id: 2, title: 'React로 게시판 만들기', author: 'user2', date: '2024-06-02', views: 8 },
  { id: 3, title: 'Styled-components 팁', author: 'user3', date: '2024-06-03', views: 5 },
  { id: 4, title: '오늘의 야구 이야기', author: 'user4', date: '2024-06-04', views: 20 },
  { id: 5, title: '자유롭게 글을 남겨보세요!', author: 'user5', date: '2024-06-05', views: 3 },
  { id: 6, title: '커뮤니티 이용 규칙', author: 'admin', date: '2024-06-06', views: 30 },
];

const POSTS_PER_PAGE = 5;

const NoticeBoard = () => {
  const [page, setPage] = useState(1);
  const totalPages = Math.ceil(mockPosts.length / POSTS_PER_PAGE);
  const pagedPosts = mockPosts.slice((page-1)*POSTS_PER_PAGE, page*POSTS_PER_PAGE);

  return (
    <BoardContainer>
      <BoardHeader>
        <Title>자유게시판</Title>
      </BoardHeader>
      <PostList>
        {pagedPosts.map(post => (
          <PostCard key={post.id}>
            <PostTitle>{post.title}</PostTitle>
            <PostMeta>
              <span>작성자: {post.author}</span>
              <span>날짜: {post.date}</span>
              <span>조회수: {post.views}</span>
            </PostMeta>
          </PostCard>
        ))}
        <button onClick={'/writing'}>글쓰기</button>
      </PostList>
      <Pagination>
        {Array.from({ length: totalPages }, (_, idx) => (
          <PageButton
            key={idx+1}
            className={page === idx+1 ? 'active' : ''}
            onClick={() => setPage(idx+1)}
          >
            {idx+1}
          </PageButton>
        ))}
      </Pagination>
    </BoardContainer>
  );
};

export default NoticeBoard;
